import React, { useState } from "react";
import Login from "./Login";
import BudgetForm from "./BudgetForm";

function App() {
  const [loggedInUser, setLoggedInUser] = useState(null);

  return (
    <div>
      {!loggedInUser ? (
        <Login onLogin={setLoggedInUser} />
      ) : (
        <BudgetForm username={loggedInUser} />
      )}
    </div>
  );
}

export default App;
