import React, { useState } from "react";
import axios from "axios";

function BudgetForm({ username }) {
  const [month, setMonth] = useState("");
  const [income, setIncome] = useState("");
  const [expenses, setExpenses] = useState("");

  const handleSubmit = async () => {
    try {
      await axios.post("http://localhost:8000/add_budget", null, {
        params: { username, month, income, expenses }
      });
      alert("Budget saved!");
    } catch (error) {
      alert("Error saving budget");
    }
  };

  return (
    <div>
      <h2>Enter Budget</h2>
      <input placeholder="Month" onChange={(e) => setMonth(e.target.value)} />
      <input type="number" placeholder="Income" onChange={(e) => setIncome(e.target.value)} />
      <input type="number" placeholder="Expenses" onChange={(e) => setExpenses(e.target.value)} />
      <button onClick={handleSubmit}>Save</button>
    </div>
  );
}

export default BudgetForm;
