import React, { useState } from "react";
import axios from "axios";
import "./BudgetForm.css"; // Importing styles

function BudgetForm({ username, onSignOut }) {
  const [month, setMonth] = useState("");
  const [income, setIncome] = useState("");
  const [expenses, setExpenses] = useState("");

  const handleSubmit = async () => {
  try {
    await axios.post("http://localhost:8000/add_budget", {
      username,
      month,
      income,
      expenses,
    });
    alert("Budget saved!");
    setMonth("");
    setIncome("");
    setExpenses("");
  } catch (error) {
    alert("Error saving budget");
  }
};


  return (
    <div className="budget-container">
      {/* Header with Logo & SignOut */}
      <header className="budget-header">
        <div className="logo-section">
          <div className="logo-placeholder">BT</div>
          <span className="company-name">Budget Tracker</span>
        </div>
        <button className="signout-btn" onClick={onSignOut}>
          Sign Out
        </button>
      </header>

      {/* Form Section */}
      <div className="budget-form-card">
        <h2>Enter Budget</h2>
        <input
          className="input-field"
          placeholder="Month"
          value={month}
          onChange={(e) => setMonth(e.target.value)}
        />
        <input
          className="input-field"
          type="number"
          placeholder="Income"
          value={income}
          onChange={(e) => setIncome(e.target.value)}
        />
        <input
          className="input-field"
          type="number"
          placeholder="Expenses"
          value={expenses}
          onChange={(e) => setExpenses(e.target.value)}
        />
        <button className="save-btn" onClick={handleSubmit}>
          Save
        </button>
      </div>
    </div>
  );
}

export default BudgetForm;
